$(function(){$('[data-toggle="popover"]').popover({trigger:'focus'});playPause();volumeSliderFade();eachSoundOffOn();volumeControl();infoBtnActiveCheck()})
function playPause(){$('.big-icon').click(function(){if(!$('#soundOffOn').hasClass('active')){var $this=$(this);var id=$this.attr('id').replace(/btn/,'');$this.toggleClass('active');if($this.hasClass('active')){$('audio[id^="sound"]')[id-1].play()}else{$('audio[id^="sound"]')[id-1].pause()}}})}
function volumeSliderFade(){$('.big-icon').click(function(){if($(this).hasClass('active')){$(this).next().filter('.volume-slider').fadeIn()}else{$(this).next().filter('.volume-slider').fadeOut()}})}
function eachSoundOffOn(){$('#soundOffOn').click(function(){$(this).toggleClass('active');if($(this).hasClass('active')){$('audio').each(function(){this.pause();$('.big-icon').removeClass('active');$('.volume-slider').fadeOut()})}})}
function volumeControl(){var sliderValue;$('.big-icon').click(function(){if($(this).hasClass('active')){var id=$(this).nextAll().slice(1,2).filter('audio').attr('id');var replace=id.replace(/sound/,'');$('#slider'+replace).slider({value:sliderValue?sliderValue:70,step:1,range:'min',min:0,max:100,slide:function(){var value=$(this).slider("value");$('audio[id^="sound"]')[replace-1].volume=(value/100);sliderValue=value}});if(!sliderValue){$('audio[id^="sound"]')[replace-1].volume=0.7}}})}
function infoBtnActiveCheck(){$('.information').click(function(){$('.information').removeClass('active');$(this).addClass('active')});setInterval(function(){if(!$('.popover').is(':visible')){if($(".information").hasClass("active")){$('.information').removeClass('active')}}},10)}